targets.expr:
(
  sum by (container, pod) (
    rate(container_cpu_usage_seconds_total{namespace=~"$namespace"}[5m])
  )
)
/
(
  sum by (container, pod) (
    kube_pod_container_resource_requests_cpu_cores{namespace=~"$namespace"}
  )
)

sum by (container, pod) (
  avg(rate(container_cpu_usage_seconds_total{namespace=~"$namespace"}[5m])) 
  * on (pod, namespace) group_left() 
  kube_pod_status_phase{phase="Running", namespace=~"$namespace"}
)

(
  sum by (container, pod) (
    rate(container_cpu_usage_seconds_total{namespace=~"$namespace"}[5m])
  )
)
/
(
  sum by (container, pod) (
    kube_pod_container_resource_limits_cpu_cores{namespace=~"$namespace"}
  )
)

(
  sum by (container, pod) (
    kube_pod_container_resource_requests_cpu_cores{namespace=~"$namespace"}
  ) 
  - 
  sum by (container, pod) (
    rate(container_cpu_usage_seconds_total{namespace=~"$namespace"}[5m])
  )
)
and
(
  sum by (container, pod) (
    kube_pod_container_resource_requests_cpu_cores{namespace=~"$namespace"}
  ) * 0.75 
  > 
  sum by (container, pod) (
    rate(container_cpu_usage_seconds_total{namespace=~"$namespace"}[5m])
  )
)
and
(
  sum by (container, pod) (
    kube_pod_container_resource_requests_cpu_cores{namespace=~"$namespace"}
  ) > 0.1
)

(
  sum by (container, pod) (
    rate(container_cpu_usage_seconds_total{namespace=~"$namespace"}[5m])
  )
  -
  sum by (container, pod) (
    kube_pod_container_resource_requests_cpu_cores{namespace=~"$namespace"}
  )
)
and
(
  sum by (container, pod) (
    rate(container_cpu_usage_seconds_total{namespace=~"$namespace"}[5m])
  )
  >
  sum by (container, pod) (
    kube_pod_container_resource_requests_cpu_cores{namespace=~"$namespace"} * 1.75
  )
)
and
(
  sum by (container, pod) (
    kube_pod_container_resource_requests_cpu_cores{namespace=~"$namespace"}
  ) 
  > 0.1
)
